useEffect(() => {
  const fetchArticles = async () => {
    setLoading(true);
    setError('');

    try {
      // Check if data is cached in sessionStorage
      const cached = sessionStorage.getItem(`articles-${category}`);
      if (cached) {
        setArticles(JSON.parse(cached));
        setLoading(false);
        return;
      }

      // Fetch from API
      const response = await axios.get(
        `https://newsapi.org/v2/top-headlines?category=${category.toLowerCase()}&country=in&apiKey=${process.env.REACT_APP_NEWS_API_KEY}`
      );

      if (!response.data.articles || response.data.articles.length === 0) {
        setError('No articles found.');
      } else {
        setArticles(response.data.articles);
        sessionStorage.setItem(`articles-${category}`, JSON.stringify(response.data.articles));
      }

    } catch (error) {
      console.error('Error fetching articles:', error);
      setError('Error fetching articles.');
    } finally {
      setLoading(false);
    }
  };

  fetchArticles();
}, [category]);
